mapper.select:
/* SQL ID: TLOPageSQL.doGetCoTechDemandInfo TLO가 관리하는 기업에 따른 기업 목록 조회: 예) '고려대학교' TLO의 경우 == Business 멤버 아이디가 멤버타입이 '고려대학교'의 경우 (9.22) *추가 예정: */
SELECT A.COMPA_NAME , CO_TD_NO , KEYWORD , SPLIT_PART(KEYWORD::VARCHAR, ',', 1) AS KEYWORD1 , SPLIT_PART(KEYWORD::VARCHAR, ',', 2) AS KEYWORD2 , SPLIT_PART(KEYWORD::VARCHAR, ',', 3) AS KEYWORD3 , SPLIT_PART(KEYWORD::VARCHAR, ',', 4) AS KEYWORD4 , SPLIT_PART(KEYWORD::VARCHAR, ',', 5) AS KEYWORD5 , BIG_CATEGORY , MID_CATEGORY , SMALL_CATEGORY , (SELECT CODE_NAME FROM tbtech_code AA WHERE AA.CODE_KEY = A.BIG_CATEGORY) AS CODE_NAME1 , (SELECT CODE_NAME FROM tbtech_code AA WHERE AA.CODE_KEY = A.MID_CATEGORY) AS CODE_NAME2 , (SELECT CODE_NAME FROM tbtech_code AA WHERE AA.CODE_KEY = A.SMALL_CATEGORY) AS CODE_NAME3 , CORPORATE_PROBLEM , HOLD_RND_INFRA , WILLINGNESS_TO_INVEST , DEPT , MANAGER_POSITION , MANAGER_NAME , MOBILEPHONE_NUM , A.biz_email , A.MEMBER_SEQNO , TECH_NEEDS , view_yn FROM TECHDEMAND_CORPORATE AS A INNER JOIN (SELECT * FROM TBLMEMBER WHERE id = '${id}') AS B ON A.COMPA_NAME = B.BIZ_NAME
AND A.view_yn = 'Y'
AND A.UPDATE > #{strDate}
AND A.UPDATE <= #{endDate}
AND A.BIG_CATEGORY = #{filterStdClassCd1}
AND A.MID_CATEGORY = #{filterStdClassCd2}
AND A.SMALL_CATEGORY = #{filterStdClassCd3}
AND A.keyword like CONCAT('%',#{keyword},'%')
INNER JOIN tblresearcher AS R on R.KEYWORD like CONCAT('%',#{keyword},'%')
ORDER BY UPDATE desc LIMIT ${rows} OFFSET (${page}-1) * 5;

/* 2023.09.18 기술분류 목록, SQL ID: TLOPageSQL.doGetCodeListInfo
*/
SELECT * FROM TBTECH_CODE WHERE CODE_DEPTH = #{depth}::INT
AND PARENT_CODE_KEY = #{parent_code_key}
ORDER BY CODE_KEY ASC

mapper.update:
/* SQL ID : TLOPageSQL.doUpdateCorporate 2023.09.19 마이페이지 기업 수정중.. */
UPDATE TECHDEMAND_CORPORATE SET COMPA_NAME = #{compa_name}, MID_CATEGORY = #{mid_category}, SMALL_CATEGORY = #{small_category}, KEYWORD = #{keyword}, TECH_NEEDS = #{tech_needs}, CORPORATE_PROBLEM = #{corporate_problem}, HOLD_RND_INFRA = #{hold_rnd_infra}, WILLINGNESS_TO_INVEST = #{willingness_to_invest}, DEPT = #{dept}, MANAGER_POSITION = #{manager_position}, MANAGER_NAME = #{manager_name}, MOBILEPHONE_NUM = #{mobilephone_num}, BIZ_EMAIL = #{biz_email}
WHERE CO_TD_NO = #{co_td_no}::INT
