mapper.select:
/* 2023.09.18 SQL ID: TLOPageSQL.doGetCodeListInfo 기술분류 목록: depth에 따라 코드를 불러오고 */
SELECT * FROM TBTECH_CODE WHERE CODE_DEPTH = #{depth}::INT
AND PARENT_CODE_KEY = #{parent_code_key}
ORDER BY CODE_KEY ASC

/* SQL ID: CoTechDemandSQL.doGetCoTechDemandInfo 기업 목록 조회 >> 변경중.. code_key를 tbtec_code테이블에서 가져옴
*/
SELECT A.COMPA_NAME , CO_TD_NO , KEYWORD , SPLIT_PART(KEYWORD::VARCHAR, ',', 1) AS KEYWORD1 , SPLIT_PART(KEYWORD::VARCHAR, ',', 2) AS KEYWORD2 , SPLIT_PART(KEYWORD::VARCHAR, ',', 3) AS KEYWORD3 , SPLIT_PART(KEYWORD::VARCHAR, ',', 4) AS KEYWORD4 , SPLIT_PART(KEYWORD::VARCHAR, ',', 5) AS KEYWORD5 , BIG_CATEGORY , MID_CATEGORY , SMALL_CATEGORY , (SELECT CODE_NAME FROM tbtech_code AA WHERE AA.CODE_KEY = A.BIG_CATEGORY) AS CODE_NAME1 , (SELECT CODE_NAME FROM tbtech_code AA WHERE AA.CODE_KEY = A.MID_CATEGORY) AS CODE_NAME2 , (SELECT CODE_NAME FROM tbtech_code AA WHERE AA.CODE_KEY = A.SMALL_CATEGORY) AS CODE_NAME3 , CORPORATE_PROBLEM , HOLD_RND_INFRA , WILLINGNESS_TO_INVEST , DEPT , MANAGER_POSITION , MANAGER_NAME , MOBILEPHONE_NUM , A.biz_email , A.MEMBER_SEQNO , TECH_NEEDS , view_yn FROM TECHDEMAND_CORPORATE AS A INNER JOIN (SELECT * FROM TBLMEMBER WHERE id = '${id}') AS B ON A.COMPA_NAME = B.BIZ_NAME

mapper.insert:
/* 2023.09.26 수정중.. SQL ID : CoTechDemandSQL.registTechDemandCo 기술수요 등록 */
INSERT INTO techdemand_corporate( co_td_no, mid_category, small_category, keyword, corporate_problem, hold_rnd_infra, willingness_to_invest, compa_name, dept, manager_position, manager_name, mobilephone_num, biz_email, member_seqno, tech_needs, tlo_seqno, big_category
) VALUES( nextval('co_techdemand__increment_seq'), #{selStdClassCd2}, #{selStdClassCd3}, #{keyword_record}, #{erro}, #{rndInfra}, #{investWilling}, #{bizName}, #{dept}, #{managerPosition}, #{managerName}, #{userMobileNo}, #{biz_email}, #{member_seqno}, #{tech_needs}, #{tlo_seqno}, #{selStdClassCd1} )
