mapper.select:
/* SQL ID : Member_v_SQL.GetMembersInfoData 사용자 조회 리스트 - 확인 중 ... */
SELECT AA.* FROM( SELECT ROW_NUMBER() OVER(ORDER BY REGIST_DATE DESC) AS RNUM, T.MEMBER_SEQNO, T.REGIST_DATE, T.MEMBER_TYPE, T.ID, T.USER_NAME, T.USER_EMAIL, T.USER_MOBILE_NO, T.BIZ_NAME, T.BIZ_EMAIL, T.BIZ_TEL_NO, T.AGREE_FLAG
FROM TBLMEMBER T WHERE T.MEMBER_TYPE = 'R' or T.MEMBER_TYPE = 'B' or T.MEMBER_TYPE = 'TLO' or T.MEMBER_TYPE = 'ADMIN' and T.delete_flag = 'N'
) AA WHERE AA.RNUM BETWEEN (#{page}::INT -1) * (#{rows}::INT) + 1 AND #{page}::INT * #{rows}::INT

mapper.update:
/* SQL ID : Member_v_SQL.doUpdateAgreement 회원관리정보 변경 - 사용자정보 */
UPDATE TBLMEMBER T SET T.AGREE_FLAG = #{mode}, T.AGREE_FLAG_DATE= NOW(), T.UPDATE_DATE= NOW(), T.UPDATE_ID = #{sessionid}
WHERE T.MEMBER_SEQNO = #{seqno}::INT AND T.MEMBER_TYPE = 'ADMIN'

mapper.select:

/*
	SQL ID : Member_v_SQL.doMemberBiz
	사업자등록증 확인
*/

	select *
	from(
	select replace(bizno ,'-','')as trans_number,vbi.*
	from voucher.nice_companyoutline_info vbi 
	)A
	where A.trans_number = #{bizRegno}
